import React, { useState } from 'react';
import './Explore.css'

const Explore = () => {
  const [selectedCategory, setSelectedCategory] = useState('India');

  const places = {
    India: [
      { name: 'Taj Mahal, Agra', image: '/images/taj-mahal.jpg', description: 'Symbol of love and a UNESCO World Heritage Site.' },
      { name: 'Jaipur, Rajasthan', image: '/images/jaipur.jpg', description: 'The Pink City known for its historic forts and palaces.' },
      { name: 'Kerala Backwaters', image: '/images/kerala.jpg', description: 'A serene network of lakes, rivers, and canals.' }
    ],
    International: [
      { name: 'Eiffel Tower, Paris', image: '/images/eiffel-tower.jpg', description: 'Iconic symbol of France and a romantic destination.' },
      { name: 'Great Wall of China', image: '/images/great-wall.jpg', description: 'A monumental structure and wonder of the world.' },
      { name: 'Sydney Opera House, Australia', image: '/images/sydney-opera-house.jpg', description: 'An architectural marvel and cultural hub.' }
    ]
  };

  return (
    <div className="p-4">
      <h1 className="text-2xl font-bold mb-4">Explore</h1>
      <div className="flex space-x-4 mb-6">
        {Object.keys(places).map((category) => (
          <button
            key={category}
            className={`px-4 py-2 rounded-lg ${
              selectedCategory === category ? 'bg-blue-500 text-white' : 'bg-gray-200'
            }`}
            onClick={() => setSelectedCategory(category)}
          >
            {category}
          </button>
        ))}
      </div>
      <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-4">
        {places[selectedCategory].map((place, index) => (
          <div key={index} className="border rounded-lg overflow-hidden shadow-lg">
            <img
              src={place.image}
              alt={place.name}
              className="w-full h-48 object-cover"
            />
            <div className="p-4">
              <h2 className="text-lg font-bold">{place.name}</h2>
              <p className="text-gray-600">{place.description}</p>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Explore;
